#include <x86intrin.h>

#define align_min(a,b) ((a) < (b) ? (a) : (b))
#define align_max(a,b) ((a) > (b) ? (a) : (b))

#define _reg    __m128i
#define REG_WIDTH 128
#define _reg_andn(a,b)  _mm_andnot_si128(a,b) //SSE2
#define _reg_and(a,b)   _mm_and_si128(a,b)    //SSE2

// word: 8bits
