//word: 16bits
#define _word              uint16_t
#define REG_WORDS          8
#define MAX_VALUE          0xFFFF
#define _reg_sll(a,b)      _mm_slli_si128(a,2*b)  //SSE2
#define _reg_srl(a,b)      _mm_srli_si128(a,2*b)  //SSE2
#define _reg_set(a)        _mm_set1_epi16(a)      //SSE2
#define _reg_add(a,b)      _mm_add_epi16(a,b)     //SSE2
#define _reg_adds(a,b)     _mm_adds_epu16(a,b)    //SSE2
#define _reg_cmpeq(a,b)    _mm_cmpeq_epi16(a,b)   //SSE2
#define _reg_cmplt(a,b)    _mm_cmplt_epi16(a,b)   //SSE2
#define _reg_min(a,b)      _mm_min_epu16(a,b)     //SSE4.1
#define _reg_blend(a,b,c)  _mm_blendv_epi8(a,b,c) //SSE4.1


int
simd_align_sse2
(
 char * qry,
 char * ref,
 int qlen,
 int rlen,
 int align_width
)
{
   size_t regcnt = 1+2*((align_width+REG_WORDS/2)/REG_WORDS);
   int    regwrd = regcnt * REG_WORDS;
   int    dif    = rlen - qlen;
   int    steps  = align_min(qlen,rlen) + dif/2;

   if (align_width < dif) return -1;

   // Aux vars.
   _reg ones = _reg_set(1);

   // Registers.
   _reg * Q  = calloc(regcnt,sizeof(_reg));
   _reg * R  = calloc(regcnt,sizeof(_reg));
   _reg * D  = malloc(regcnt*sizeof(_reg));
   _reg * S  = malloc(regcnt*sizeof(_reg));

   // Register byte access.
   _word * q  = (_word *) Q;
   _word * r  = (_word *) R;
   _word * d  = (_word *) D;
   _word * s  = (_word *) S;

   // Initialize Q,R,S,D.
   int k;
   // Initialize Q.
   for (k = regwrd/2 - 1; k >= qlen; k--) q[regwrd/2 - 1 - k] = 'Q';
   for (; k >= 0; k--) q[regwrd/2 - 1 - k] = qry[k];
   for (k = regwrd/2; k < regwrd; k++) q[k] = 'Q';
   // Initialize R.
   for (k = 0; k < regwrd/2; k++) r[k] = 'R';
   for (k = 0; k < regwrd/2 && k < rlen; k++) r[regwrd/2 + k] = ref[k];
   for (; k < regwrd/2; k++) r[regwrd/2 + k] = 'R';
   // Initialize S.
   for (int i = 0, v = regwrd/2; i < regwrd/2; i++)
      s[i] = s[regwrd-1-i] = v--;
   // Initialize D.
   for (int i = 0, v = regwrd/2; i < regwrd/2; i++) {
      d[regwrd-1-i] = v--;
      d[i] = v;
   }

   // Alignment.
   for (int i = 0; i < steps; i++) {
      // TODO: at the end of the alignment, there will be parts of the
      // diagonal that fall off the matrix, in that case the values of j
      // can be restricted only to these words that represent matrix values.

      // Step Right D,S -> D.
      _word R_in = (regwrd/2 + i >= rlen ? 'R' : ref[regwrd/2 + i]);
      _word S_in = MAX_VALUE;
      for (int j = regcnt-1; j >= 0; j--) {
         // Update R.
         r = (_word *)&(R[j]);
         _word R_out = r[0];
         R[j] = _reg_srl(R[j], 1);
         r[REG_WORDS-1] = R_in;
         R_in = R_out;
         // Update upper and side scores (gap).
         _reg G = _reg_adds(S[j], ones);
         // Compare Q and R (match/mismatch).
         _reg M = _reg_andn(_reg_cmpeq(Q[j],R[j]), ones);
         M = _reg_adds(M,D[j]);
         // Minimum between G and M.
         M = _reg_min(G, M);
         // Right shift the gaps and insert S_in.
         _word * g = (_word *)&G;
         _word S_out = g[0];
         G = _reg_srl(G, 1);
         g[REG_WORDS-1] = S_in;
         S_in = S_out;
         // Minimum between G and M.
         D[j] = _reg_min(G, M);
      }

      // Step Down S,D -> S.
      _word Q_in = (regwrd/2 + i >= qlen ? 'Q' : qry[regwrd/2 + i]);
      _word D_in = MAX_VALUE;
      for (int j = 0; j < regcnt; j++) {
         // Update Q.
         q = (_word *)&(Q[j]);
         _word Q_out = q[REG_WORDS-1];
         Q[j] = _reg_sll(Q[j], 1);
         q[0] = Q_in;
         Q_in = Q_out;
         // Update upper and side scores (gap).
         _reg G = _reg_adds(D[j], ones);
         // Compare Q and R (match/mismatch).
         _reg M = _reg_andn(_reg_cmpeq(Q[j],R[j]), ones);
         M = _reg_adds(M,S[j]);
         // Minimum between G and M.
         M = _reg_min(G, M);
         // Left shift the gaps and insert D_in.
         _word * g = (_word *)&G;
         _word D_out = g[REG_WORDS-1];
         G = _reg_sll(G, 1);
         g[0] = D_in;
         D_in = D_out;
         // Minimum between G and M.
         S[j] = _reg_min(G, M);
      }
   }

   // Return right-bottom corner.
   int byte = regwrd/2-1+dif/2+dif%2;

   if (dif%2) return s[byte];
   else       return d[byte];

}

